import React, { useState } from 'react';
import Modal from './Modal';

const Sidebar = () => {
  const [modalTrigger, setModalTrigger] = useState(true);
  return (
    <>
      <aside class="top-[5%] left-[5%] p-4 fixed">
        <button class="p-4 bg-[#c4d5dd6e] text-2xl rounded-sm" type="submit" onClick={()=>setModalTrigger(true)}>
          Добавить запись
        </button>
      </aside>
      <Modal modalTrigger={modalTrigger} setModalTrigger={setModalTrigger} />
    </>
  );
};

export default Sidebar;
