import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import AuthService from '../../services/testAuServ';

export const fetchLogin = createAsyncThunk('user/login', async (email, password) => {
  try {
    const response = await AuthService.login(email, password);
    console.log(response);
    localStorage.setItem('token', response.data.accessToken);
  } catch (error) {
    console.log('Error', error);
  }
});

export const fetchRegistration = createAsyncThunk('user/registration', async ({email, password}) => {
  try {
    const response = await AuthService.login(email, password);
    console.log(response);
    localStorage.setItem('token', response.data.accessToken);
  } catch (error) {
    console.log('Error', error);
  }
});

const initialState = {
  isAuth: false,
};

export const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchLogin.fulfilled, (state) => {
      state.isAuth = true;
    });
    builder.addCase(fetchRegistration.fulfilled, (state) => {
      state.isAuth = true;
    });
  },
});

// Action creators are generated for each case reducer function
export const {} = postsSlice.actions;

export default postsSlice.reducer;
