import React, { useState } from 'react';
import LoginForm from '../components/LoginFrom';
import RegistrationForm from '../components/RegistrationForm';

function Login() {
  const [login, setLogin] = useState('login');

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <main className="container m-auto">
      <section class="flex justify-center items-center mt-16">
        <form
          action=""
          class="flex-col flex space-y-6 w-[250px] form py-8 px-4"
          onSubmit={handleSubmit}>
          <div class="flex justify-center">
            <h2
              class={`hover:text-gray-400 cursor-pointer ${
                login === 'login' ? 'active_btn_log' : ''
              }`}
              onClick={() => setLogin('login')}>
              Login
            </h2>
            /
            <h2
              class={`hover:text-gray-400 cursor-pointer ${
                login === 'registration' ? 'active_btn_log' : ''
              }`}
              onClick={() => setLogin('registration')}>
              Registration
            </h2>
          </div>
          {login === 'login' ? <LoginForm /> : <RegistrationForm />}
        </form>
      </section>
    </main>
  );
}

export default Login;
