import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  postsList: [
    {
      id: 'fcbe8ffd-b395-4b94-9ccb-a62956d463b8',
      title: 'fsdsdf',
      desc: 'dsf',
      status: true,
    },
    {
      id: '4ce7f1f7-04d9-46e7-a8ac-1b6fb6fddbc7',
      title: 'fsdsdf',
      desc: 'dsfdfg',
      status: true,
    },
    {
      id: '92622da1-9e50-48fb-a0ac-00c5b570cd13',
      title: 'fsdsdf',
      desc: 'dsfdfgsdf',
      status: false,
    },
    {
      id: 'b8153243-c976-4e65-bd72-1b53ac824499',
      title: 'as',
      desc: 'dsfdfgsdf',
      status: false,
    },
    {
      id: '77f5db7c-5cf1-4e47-9d11-b4e7e73151d4',
      title: 'adss',
      desc: 'dsfdfgsdf',
      status: false,
    },
  ],
};

export const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    addPost: (state, action) => {
      state.postsList = [...state.postsList, ...action.payload];
    },
    deletePost: (state, action) => {
      state.postsList = state.postsList.filter((item) => {
        return item.id != action.payload.id;
      });
    },
    updatePost: (state, action) => {
      //Обновляю статус
      state.postsList = state.postsList.map((item) => {
        if (item.id === action.payload.id) {
          return { ...item, status: !item.status };
        }
        return item;
      });
    },
  },
});

// Action creators are generated for each case reducer function
export const { addPost, deletePost, updatePost } = postsSlice.actions;

export default postsSlice.reducer;
