import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const addPost = createAsyncThunk('posts/addPost', async (data) => {
  try {
    const response = await axios.post('http://localhost:8080/api/addPost', data);
    console.log(response);
    dispa
    getPosts()
  } catch (error) {
    console.log('Error', error);
  }
});
export const remPost = createAsyncThunk('posts/remPost', async (data) => {
  try {
    const response = await axios.post('http://localhost:8080/api/remPost', data);
    console.log(response);
  } catch (error) {
    console.log('Error', error);
  }
});

export const getPosts = createAsyncThunk('posts/getPosts', async () => {
  try {
    const response = await axios.get('http://localhost:8080/api/getPosts');
    console.log(response);
    return response.data;
  } catch (error) {
    console.log('Error', error);
  }
});

const initialState = {
  postsList: [],
  activeList: [],
  inactiveList: [],
};

export const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    
    updatePost: (state, action) => {
      //Обновляю статус
      console.log(action.payload.status);
      state.postsList = state.postsList.map((item) => {
        if (item.id === action.payload.id) {
          return { ...item, status: !item.status };
        }
        return item;
      });
      //Перекид в новый массив по статусу
      if (action.payload.status === true) {
        state.activeList = state.activeList.filter((item) => {
          return item.id != action.payload.id;
        });
        state.inactiveList = [...state.inactiveList, { ...action.payload, status: false }];
      } else {
        state.inactiveList = state.inactiveList.filter((item) => {
          return item.id != action.payload.id;
        });
        state.activeList = [...state.activeList, { ...action.payload, status: true }];
      }
    },
  },
  extraReducers: (builder) => {
    builder.addCase(getPosts.fulfilled, (state, action) => {
      if (action.payload) {
        state.postsList = action.payload;
      } else {
        state.postsList = [];
      }
    });
  },
});

// Action creators are generated for each case reducer function
export const { updatePost } = postsSlice.actions;

export default postsSlice.reducer;
