import React from 'react';
import useAuth from '../hook/useAuth';
import { useDispatch } from 'react-redux';
import { sendPost } from '../redux/slices/postsSlice';

const UserInterface = () => {
  const { user, signIn } = useAuth();

  const submitHandler = (e) => {
    e.preventDefault();
    const form = e.target;
    const user = form.username.value;
    signIn(user, () => {});
  };
  const dispatch = useDispatch();
  const handlerTest = () => {
    dispatch(sendPost([{ key: 'qwewqe' }]));
  };
  return (
    <>
      <div className="ml-4">
        <h2 className="text-center mb-2">
          {user ? <span>Вы вошли как: {user}</span> : <span>Войти</span>}
        </h2>
        <form className="shadow-2xl " onSubmit={submitHandler}>
          <input type="text" placeholder="User" className="p-4 w-full " name="username" />
          <button type="submit" className="btn hover:bg-[#8f989c6e] w-full">
            Зайти
          </button>
        </form>
        <button
          type="submit"
          className="btn hover:bg-[#8f989c6e] w-full"
          onClick={() => handlerTest()}>
          Test
        </button>
      </div>
    </>
  );
};

export default UserInterface;
