import React, { useRef, useState } from 'react';

const LoginForm = () => {
  
  const loginRef = useRef(123);
  const passwordRef = useRef(null);

  const [first, setfirst] = useState(loginRef)
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(event.login.value);
  };
  console.log(loginRef.current);

  return (
    <form action="" class="space-y-6" onSubmit={handleSubmit}>
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Login"
        name="login"
        useRef={loginRef}
        onChange={(e)=>{console.log(e.target.value);}}
      />
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Password"
        useRef={passwordRef}
      />

      <button type="submit" class="btn_log">
        Войти
      </button>
      <div onClick={()=>{setfirst(first+1)}}>
        fdsfds
      </div>
    </form>
  );
};

export default LoginForm;
