import React from 'react';

const FilterBarPosts = () => {
  const createFilterElem = (title) => {
    return (
      <li className="filter__elem">
        <h1 className="text-4xl filter__elem">{title}</h1>
      </li>
    );
  };
  return (
    <ul className="bg-white border-[1px] border-black rounded-[25px] flex text-center overflow-hidden mb-4 ">
      {createFilterElem('Все')}
      {createFilterElem('Выполненные')}
      {createFilterElem('Невыполненные')}
    </ul>
  );
};

export default FilterBarPosts;
