import React, { useRef } from 'react';

const LoginForm = () => {
  const loginRef = useRef(null);
  const passwordRef = useRef(null);
  const loginFormHandler = () => {
    console.log(loginRef.current.value);
  };
  return (
    <form action=""
    class="flex-col flex space-y-6 w-[250px] form py-8 px-4"
    onSubmit={handleSubmit}>
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Login"
        useRef={loginRef}
      />
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Password"
        useRef={passwordRef}
      />

      <button
        class="btn_log"
        onClick={() => {
          loginFormHandler();
        }}>
        Войти
      </button>
    </form>
  );
};

export default LoginForm;
