import React, { useRef } from 'react';

const LoginForm = () => {
  const loginRef = useRef(null);
  const passwordRef = useRef(null);
  const loginFormHandler = () => {
    console.log(loginRef.valueOf());
  };
  return (
    <>
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Login"
        useRef={loginRef}
      />
      <input
        class="py-2 px-3 w-full border rounded-2xl bg-[#f2f2f2]"
        type="text"
        placeholder="Password"
        useRef={passwordRef}
      />

      <button
        type="submit"
        class="btn_log"
        onClick={() => {
          loginFormHandler();
        }}>
        Войти
      </button>
    </>
  );
};

export default LoginForm;
