import React, { useRef } from 'react';
import { useDispatch } from 'react-redux';
import { addPost } from '../redux/slices/slice';
import { useId } from 'react';

const Modal = ({ modalTrigger, setModalTrigger }) => {
  const dispatch = useDispatch();
  const titleRef = useRef('');
  const descRef = useRef('');
  const id = useId()
  const handlerGeneratePost = (id) => {
    console.log(titleRef.current.value);
    console.log(descRef.current);
    
    dispatch(addPost([{ title: titleRef.current.value, desc: descRef.current.value }]));
  };
  return (
    <div
      className={`modal__overlay ${modalTrigger ? 'modal_active' : ''}`}
      onClick={() => setModalTrigger(false)}>
      <div className="modal__content" onClick={(e) => e.stopPropagation()}>
        <div className="mt-4 p-4">
          <input
            className="modal_input"
            type="text"
            ref={titleRef}
            placeholder="Введите заголовок"
          />
          <input className="modal_input" type="text" ref={descRef} placeholder="Описание" />
          <div className="text-right mr-4 mt-6">
            <button className="btn" type="submit" onClick={() => handlerGeneratePost(id)}>
              Добавить
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
