import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { useDispatch } from 'react-redux';

export const sendPosts = createAsyncThunk('posts/sendPosts', async (data) => {
  try {
    const response = await axios.post('http://localhost:8080/saveData', data);
    console.log(response);
  } catch (error) {
    console.log('Error', error);
  }
});
export const getPosts = createAsyncThunk('posts/sendPosts', async (data) => {
  try {
    const response = await axios.post('http://localhost:8080/saveData', data);
    console.log(response);
  } catch (error) {
    console.log('Error', error);
  }
});


const initialState = {
  postsList: [],
  activeList: [],
  inactiveList: [],
};

export const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    addPost: (state, action) => {
      state.postsList = [...state.postsList, ...action.payload];
      state.inactiveList = [...state.inactiveList, ...action.payload];
    },
    deletePost: (state, action) => {
      state.postsList = state.postsList.filter((item) => {
        return item.id != action.payload.id;
      });
    },
    updatePost: (state, action) => {
      //Обновляю статус
      console.log(action.payload.status);
      state.postsList = state.postsList.map((item) => {
        if (item.id === action.payload.id) {
          return { ...item, status: !item.status };
        }
        return item;
      });
      //Перекид в новый массив по статусу
      if (action.payload.status === true) {
        state.activeList = state.activeList.filter((item) => {
          return item.id != action.payload.id;
        });
        state.inactiveList = [...state.inactiveList, { ...action.payload, status: false }];
      } else {
        state.inactiveList = state.inactiveList.filter((item) => {
          return item.id != action.payload.id;
        });
        state.activeList = [...state.activeList, { ...action.payload, status: true }];
      }
    },
  },
});

// Action creators are generated for each case reducer function
export const { addPost, updatePost, deletePost } = postsSlice.actions;

export default postsSlice.reducer;
