import React, { useState } from 'react';
import DetalPost from './DetalPost';
import { useSelector } from 'react-redux';
import FilterBarPosts from './FilterBarPosts';

const PostsList = () => {
  const { postsList, activeList, inactiveList } = useSelector((state) => state.posts);
  const [filterValue, setFilterValue] = useState('all');


  const handlePostList = (posts,key) => {
    if (key === 'accept'){
      return posts
    }
    return posts ? (
      posts.map((item) => <DetalPost key={item._id} post={item} />)
    ) : (
      <div className="text-center w-full ">
        <h2 className=" font-bold text-6xl">Постов пока нет</h2>
      </div>
    );
  };

  return (
    <section className="w-[1000px]">
      <FilterBarPosts setFilterValue={setFilterValue} />
      <ul className="space-y-4 w-full">
        {filterValue === 'all'
          ? handlePostList(postsList,'all')
          : filterValue === 'accept'
          ? handlePostList(activeList)
          : handlePostList(inactiveList)}
      </ul>
    </section>
  );
};

export default PostsList;
