import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const addPost = createAsyncThunk('posts/addPost', async (data, thunkAPI) => {
  try {
    const response = await axios.post('http://localhost:8080/api/addPost', data);
    console.log(response);
    thunkAPI.dispatch(getPosts());
  } catch (error) {
    console.log('Error', error);
  }
});
export const remPost = createAsyncThunk('posts/remPost', async (data, thunkAPI) => {
  try {
    const response = await axios.post('http://localhost:8080/api/remPost', data, thunkAPI);
    console.log(response);
    thunkAPI.dispatch(getPosts());
  } catch (error) {
    console.log('Error', error);
  }
});

export const getPosts = createAsyncThunk('posts/getPosts', async () => {
  try {
    const response = await axios.get('http://localhost:8080/api/getPosts');
    console.log(response);
    return response.data;
  } catch (error) {
    console.log('Error', error);
  }
});

export const updatePost = createAsyncThunk('posts/updatePost', async (data, thunkAPI) => {
  try {
    const response = await axios.post('http://localhost:8080/api/updatePost', data);
    console.log(response);
    thunkAPI.dispatch(getPosts());
  } catch (error) {
    console.log('Error', error);
  }
});

const initialState = {
  postsList: [],
  active: 0,
  inactive: 0,
};

export const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getPosts.fulfilled, (state, action) => {
      if (action.payload) {
        state.active = 0;
        state.inactive = 0;
        state.postsList = action.payload;
        action.payload.map((item) => {
          if (item.status === true) {
          }
        });
      } else {
        state.postsList = [];
      }
    });
  },
});

// Action creators are generated for each case reducer function
export const {} = postsSlice.actions;

export default postsSlice.reducer;
