import React, { useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import { addPost } from '../redux/slices/postsSlice';
import { v4 as uuidv4 } from 'uuid';

const Modal = ({ modalTrigger, setModalTrigger }) => {
  const dispatch = useDispatch();
  const titleRef = useRef('');
  const descRef = useRef('');

  const MAX_SYMBOL_DESC = 10;

  const [symbolDesc, setSymbolDesc] = useState(MAX_SYMBOL_DESC);

  const handlerGeneratePost = () => {
    const id = uuidv4();
    dispatch(
      addPost([
        { id: id, title: titleRef.current.value, desc: descRef.current.value, status: false },
      ]),
    );
  };
  return (
    <div
      className={`modal__overlay ${modalTrigger ? 'modal_active' : ''}`}
      onClick={() => setModalTrigger(false)}>
      <div className="modal__content" onClick={(e) => e.stopPropagation()}>
        <div className="mt-4 p-4">
          <input
            className="modal_input"
            type="text"
            ref={titleRef}
            placeholder="Введите заголовок"
          />

          <input
            className="modal_input"
            type="text"
            ref={descRef}
            placeholder="Описание"
            onChange={() => {
              setSymbolDesc(MAX_SYMBOL_DESC - descRef.current.value.length);
            }}
          />
          <p className="ml-2 text-[10px]">Вы можете ввести ещё {symbolDesc} символов</p>
          <div className="text-right mr-4 mt-6">
            <button className="btn" type="submit" onClick={() => handlerGeneratePost()}>
              Добавить
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
