import React, { useState } from 'react';
import Modal from './Modal';
import { useSelector } from 'react-redux';

const Sidebar = () => {
  const [modalTrigger, setModalTrigger] = useState(false);
  const { activeList } = useSelector((state) => state.posts);
  return (
    <>
      <aside className="top-[5%] left-[5%] p-4 fixed shadow-2xl">
        <div className="flex justify-center mb-4 space-x-6">
          <h4>complite : </h4>
          <h4>expect : </h4>
        </div>
        <button
          className="btn hover:bg-[#8f989c6e]"
          type="submit"
          onClick={() => setModalTrigger(true)}>
          Добавить запись
        </button>
      </aside>
      <Modal modalTrigger={modalTrigger} setModalTrigger={setModalTrigger} />
    </>
  );
};

export default Sidebar;
