import React, { useState } from 'react';
import Modal from './Modal';
import { useDispatch, useSelector } from 'react-redux';
import useAuth from '../hook/useAuth';


const Sidebar = () => {
  const { user } = useAuth();
  const [modalTrigger, setModalTrigger] = useState(false);
  const { activeList, inactiveList } = useSelector((state) => state.posts);

  const modalHandler = () => {
    if (user) {
      setModalTrigger(true);
    } else {
      alert('Сперва войдите в аккаунт');
    }
  };
  return (
    <>
      <aside className="top-[5%] left-[5%] p-4 fixed shadow-2xl">
        <div className="flex justify-center mb-4 space-x-6">
          <h4>active : {activeList.length} </h4>
          <h4>inactive : {inactiveList.length}</h4>
        </div>
        <button className="btn hover:bg-[#8f989c6e]" type="submit" onClick={() => modalHandler()}>
          Добавить запись
        </button>
      </aside>
      <Modal modalTrigger={modalTrigger} setModalTrigger={setModalTrigger} />
    </>
  );
};

export default Sidebar;
